function romanEncoder($number) {
            $roman = "";
            $numerals = array("M" => 1000, "CM" => 900, "D" => 500, "CD" => 400, "C" => 100, "XC" => 90, "L" => 50, "XL" => 40, "X" => 10, "IX" => 9, "V" => 5, "IV" => 4, "I" => 1);

            foreach ($numerals as $numeral => $value) {
                $roman .= str_repeat($numeral, floor($number / $value));
                $number = $number % $value;
            }

            return $roman;
        }

