function turnClues(turn, clues) {
    let c = [];
    for (let i = 0; i < 16; i++) {
        if (i >= turn * 4) c.push(clues[i]);
    }
    for (let i = 0; i < 16; i++) {
        if (i < turn * 4) c.push(clues[i]);
    }
    return c;
}

