function turnClues(turn, clues) {
    let c = [];
    for (let i = 0; i < 24; i++) {
        if (i >= turn * 6) c.push(clues[i]);
    }
    for (let i = 0; i < 24; i++) {
        if (i < turn * 6) c.push(clues[i]);
    }
    return c;
}

