    //6 tests for straights, 6 tests for straight flushes
    let straightOnly = Array.from({length:6},e => makeStraights()),
    	straightFlush = Array.from({length:6},e => makeStraights(true));

    //pairs,2pairs,trips,4kind,fullhouse
    function make234(r){
    	let usedList = [];
    	let zs = r.reduce((a,e) => {
    		let g = randomMaker(13);
    		while (usedList[g]){g = randomMaker(13)}
    		usedList[g] = true;
    		let rsuit = suits.slice(randomMaker(4));
    		return a.concat(Array(e).fill(g).map((v,c) => `${nums[v]}${rsuit[c]}`));
    	},[]);
    	while (zs.length < 7){
    		let rz = randomMaker(13);
    		while (usedList[rz]){rz = randomMaker(13)}
    		usedList[rz] = true;
    		let gz = makeRandomCard(rz);
    		while (zs.includes(gz)){gz = makeRandomCard(rz)}
    		zs.push(gz)}
    	return zs;
    }
    //6 tests each for pairs, two pairs, triples, 4-of-a-kind, and full house
    let [pairT,twopairT,tripleT,fourT,fullhouseT] = [[2],[2,2],[3],[4],[2,3]]
    .map(e => Array.from({length:6},v => make234(e)));

    function flushMaker(){
    	let usedList = [];
    	let difSet = [];
    	let g = randomMaker(4);
    	while (difSet.length < 5){
    		let z = randomMaker(13);
    		while (usedList[z]){z = randomMaker(13)}
    		usedList[z] = true;
    		difSet.push(z)}
    	difSet = difSet.map(e => `${nums[e]}${suits[g]}`);
    	while (difSet.length < 7){
    		let zz = makeRandomCard();
    		while (difSet.includes(zz)){zz = makeRandomCard()}
    		difSet.push(zz)}
    	return difSet;
    }

    function highhand(){
    	let usedList = [];
    	let usedSuits = [4,4,4,4];
    	let ar = [];
    	let suitsar = [];
    	while (ar.length < 7){
    		let g = randomMaker(13);
    		while (usedList[g]){g = randomMaker(13)}
    		usedList[g] = true;
    		ar.push(g)}
    	while (suitsar.length < 7){
    		let g = randomMaker(4);
    		while (!usedSuits[g]){g = randomMaker(4)}
    		usedSuits[g]--;
    		suitsar.push(g)}
    	return ar.map((e,i) => `${nums[e]}${suits[suitsar[i]]}`);
    }

    //6 tests for flushes and high-card hands
    let flushtests = Array.from({length:6},e => flushMaker());
    let highhandtests = Array.from({length:6},e => highhand());

    let aggregateList =[straightOnly,straightFlush,pairT,twopairT,tripleT,fourT,fullhouseT,flushtests,highhandtests]
    .map(e => e.map(v => {
    	let cc = randomMaker(7),
    		gc = randomSort(v.slice(cc).concat(v.slice(0,cc)));
    	return [gc.slice(0,2),gc.slice(2)]}));

    let finalRandomList = [];
    while (finalRandomList.length < 54){
    	let n = randomMaker(aggregateList.length);
    	if (!aggregateList[n].length){aggregateList.splice(n,1); continue}
    	finalRandomList.push(aggregateList[n].pop());
    }

