  all.splice(idx,1)
  idx=rand(0,all.length-1)
  r[x+all[idx][0]][y+all[idx][1]]="G"
  all.splice(idx,1)
  idx=rand(0,all.length-1)
  r[x+all[idx][0]][y+all[idx][1]]="Y"
  all.splice(idx,1)

  var [x,y]=find9(),all=[[0,0],[0,1],[0,2],[1,0],[1,1],[1,2],[2,0],[2,1],[2,2]]
  if(x==-1) return rndtest()
  var idx=rand(0,all.length-1)
  r[x+all[idx][0]][y+all[idx][1]]="r"
  all.splice(idx,1)
  idx=rand(0,all.length-1)
  r[x+all[idx][0]][y+all[idx][1]]="g"
  all.splice(idx,1)
  idx=rand(0,all.length-1)
  r[x+all[idx][0]][y+all[idx][1]]="y"
  all.splice(idx,1)
  var rs=r.map(x=>x.join``).join`\n`
  return an(rs)?rs:rndtest()

  function find9(){
    find9count++
    if(find9count>limit) return [-1,-1]
    var x=rand(0,h-4),y=rand(0,w-4)
    for(var i=0;i<3;i++)for(var j=0;j<3;j++) if(r[x+i][y+j]!==" ") return find9()
