function an(gameMap){
  var map=gameMap.split`\n`.map(x=>x.split``.map(x=>x.trim())),h=map.length,w=map[0].length,
      sR,sG,sY,er,eg,ey,rec={},bingo=false,empty={},countmove=0,limit=5000
  for(var i=1;i<h-1;i++){
    for(var j=1;j<w-1;j++){
      var c=map[i][j]
      if(c=="R") sR=[i,j]
      if(c=="G") sG=[i,j]
      if(c=="Y") sY=[i,j]
      if(c=="r") er=[i,j]
      if(c=="g") eg=[i,j]
      if(c=="y") ey=[i,j]
    }
  }
  var oldp=[sR,sG,sY].join()
  move(sR,sG,sY,oldp,"o")
  //console.log(bingo)
  return bingo
  var path="",nowp=[er,eg,ey].join()
  while(nowp!=oldp){
    //console.log(rec[nowp])
    var s=rec[nowp]
    path=s[0]+path
    nowp=s.slice(1)
  }
  //console.log(path.length)
  return path

  function move(d1,d2,d3,shape,where){
    countmove++
    if(countmove>limit) return;
    rec[shape]=where
    //console.log(shape,where)
    if(bingo) return;
    if(isBingo(d1,d2,d3)){
      bingo=true
      return;
    }
    var od=ordernow(d1,d2,d3)

    for(var i=0;i<4;i++){
      var can=canmoveto(d1,d2,d3,od[i])
      if(can){//"↑↓→←"
        var s=can.join()
        if(!rec[s]) move(can[0],can[1],can[2],s,od[i]+shape)
      }
    }

  }
  function ordernow(d1,d2,d3){
    var r=[]
    if(d1[1]<er[1]&&d2[1]<eg[1]&&d3[1]<ey[1]) r[0]="→",r[2]="←"
    else r[0]="←",r[2]="→"
    if(d1[0]<er[0]&&d2[0]<eg[0]&&d3[0]<ey[0]) r[1]="↓",r[3]="↑"
    else r[1]="↑",r[3]="↓"
    return r
  }
  function canmoveto(d1,d2,d3,dire){

    var x1=dire=="↑"? -1:dire=="↓"?1:0,
        y1=dire=="←"? -1:dire=="→"?1:0

    //???
    var em=[Math.min(d1[0]+x1,d2[0]+x1,d3[0]+x1),Math.min(d1[1]+y1,d2[1]+y1,d3[1]+y1),Math.max(d1[0]+x1,d2[0]+x1,d3[0]+x1),Math.max(d1[1]+y1,d2[1]+y1,d3[1]+y1)]
    var ems=`${em[0]},${em[1]},${em[2]},${em[3]}`
    if(empty[ems]===undefined){
      var found=false
      for(var i=Math.max(0,em[0]-1);!found&&i<=Math.min(h-1,em[2]+1);i++) for(var j=Math.max(0,em[1]-1);!found&&j<=Math.min(w-1,em[3]+1);j++){
        if(map[i][j]=="*"||map[i][j]=="|"||map[i][j]=="-") found=true
      }
      empty[ems]=found
    }
    else if(empty[ems]===false) return false
    //???
    var can1=canmove(d1,x1,y1),
        can2=canmove(d2,x1,y1),
        can3=canmove(d3,x1,y1),
        nd1=can1||[d1[0],d1[1]],
        nd2=can2||[d2[0],d2[1]],
        nd3=can3||[d3[0],d3[1]]
    return can1||can2||can3?Math.max(nd1[0],nd2[0],nd3[0])-Math.min(nd1[0],nd2[0],nd3[0])<3&&Math.max(nd1[1],nd2[1],nd3[1])-Math.min(nd1[1],nd2[1],nd3[1])<3?[nd1,nd2,nd3]:false:false

    function canmove(d,x,y){
      var nx=d[0]+x,ny=d[1]+y,c=map[nx][ny]
      if(c=="-"||c=="|"||c=="*") return false
      if(nx==d1[0]&&ny==d1[1]) return canmove(d1,x,y)?[nx,ny]:false
      if(nx==d2[0]&&ny==d2[1]) return canmove(d2,x,y)?[nx,ny]:false
      if(nx==d3[0]&&ny==d3[1]) return canmove(d3,x,y)?[nx,ny]:false
      return [nx,ny]
    }
  }
  function isBingo(d1,d2,d3){
    return d1[0]==er[0]&&d1[1]==er[1]&&
           d2[0]==eg[0]&&d2[1]==eg[1]&&
           d3[0]==ey[0]&&d3[1]==ey[1]
  }
}

