    }
    function rndmap(){
      var jl= ~~(100*Math.random())%5
      if (jl==0) return rndmaps();
      while (true){
        var tm=rndmaps(),anan=an(tm)
        if (anan.length>0) return tm
      }
    }

    function an(gamemap){
      var map=gamemap.replace(/E/g,"e").split("\n").map(x=>x.split("")),
          w=map[0].length,h=map.length,allsol=[],seed=[]
      for (var i=0;i<w;i++) map[0][i]=".",map[h-1][i]="."
      for (var i=0;i<h;i++) map[i][0]=".",map[i][w-1]="."
      var omap=map.map(x=>x.join("")).join("\n")
      map=myremoveEO(map,w,h)
      if (mycheckpath(map,w,h)=="") return [map.map(x=>x.join("")).join("\n").replace(/e/g,"E")]
      else{
        seed.push([0,omap])
        while(seed.length>0&&allsol.length==0){
          var tmp=[]
          for (var [eo,s] of seed){
            var tmap=s.split("\n").map(x=>x.split(""))
            for (var i=1;i<h;i+=2) for (var j=1;j<w;j+=2) if (tmap[i][j]=="e"){
              var tt=tmap.map(x=>x.slice())
              tt[i][j]="E";
              tmp.push([eo+1,tt.map(x=>x.join("")).join("\n")])
              tt=myremoveEO(tt,w,h)
              if (mycheckpath(tt,w,h)=="") {
                var st=tt.map(x=>x.join("")).join("\n").replace(/e/g,"E")
                if (allsol.indexOf(st)==-1) allsol.push(st)
              }
            }
          }
          seed=tmp
        }
      }
      //console.log(allsol.length)
      return allsol
    }

    function myremoveEO(map,w,h,i,j){
      for (var i=1;i<h;i+=2){
        for (var j=1;j<w;j+=2){
          if (map[i][j]=="O"||map[i][j]=="e"){
            if (i==1) map[0][j]=" ",map[0][j-1]=" ",map[0][j+1]=" "
            if (i==h-2) map[h-1][j]=" ",map[h-1][j-1]=" ",map[h-1][j+1]=" "
            if (j==1) map[i-1][0]=" ",map[i][0]=" ",map[i+1][0]=" "
            if (j==w-2) map[i-1][w-1]=" ",map[i][w-1]=" ",map[i+1][w-1]=" "
            if (i!=1&&(map[i-2][j]=="I"||map[i-2][j]=="E")) map[i-1][j-1]=".",map[i-1][j]=".",map[i-1][j+1]="."
            if (i!=h-2&&(map[i+2][j]=="I"||map[i+2][j]=="E")) map[i+1][j-1]=".",map[i+1][j]=".",map[i+1][j+1]="."
            if (j!=1&&(map[i][j-2]=="I"||map[i][j-2]=="E")) map[i-1][j-1]=".",map[i][j-1]=".",map[i+1][j-1]="."
            if (j!=w-2&&(map[i][j+2]=="I"||map[i][j+2]=="E")) map[i-1][j+1]=".",map[i][j+1]=".",map[i+1][j+1]="."
          }
        }
      }
      return map
    }
    function mycheckpath(mp,w,h){
      var map=mp.map(x=>x.slice()),alltype="updown,upleft,upright,downleft,downright,leftright"
      var br=true
      for (var i=0;i<h&&br;i++) for (var j=0;j<w&&br;j++) if (map[i][j]==".") br=false;
      i--; j--;
      var ii=i,jj=j,step=0,alldot=(map+"").match(/\./g).length;
      while(true){
        var type="";
        if(i-1>-1) type+=map[i-1][j]=="."||map[i-1][j]=="x"?"up":""
        if(i+1<h)  type+=map[i+1][j]=="."||map[i+1][j]=="x"?"down":""
        if(j-1>-1) type+=map[i][j-1]=="."||map[i][j-1]=="x"?"left":""
        if(j+1<w)  type+=map[i][j+1]=="."||map[i][j+1]=="x"?"right":""
        if (alltype.indexOf(type)==-1) return "e1"
        step++
        map[i][j]="x"
        var dirt="";
        if(i-1>-1) dirt+=map[i-1][j]=="."?"up":""
        if(i+1<h)  dirt+=map[i+1][j]=="."?"down":""
        if(j-1>-1) dirt+=map[i][j-1]=="."?"left":""
        if(j+1<w)  dirt+=map[i][j+1]=="."?"right":""
        if (dirt=="") break
        else if(dirt.indexOf("up")>-1&&map[i-1][j]==".") i--
        else if(dirt.indexOf("down")>-1&&map[i+1][j]==".") i++
        else if(dirt.indexOf("left")>-1&&map[i][j-1]==".") j--
        else if(dirt.indexOf("right")>-1&&map[i][j+1]==".") j++
        else break
      }

      return step==alldot?"":"e2";
    }

