function RN(n){
 	return Math.random()*n|0   
}

const testGen = n => {
	const r = [];
	const colors = 'RGBY',
		  gemGem = _ => [colors[RN(4)],colors[RN(4)]],
		  gemCrash = _ => [colors[RN(4)].toLowerCase(),colors[RN(4)]],
		  crashCrash = _ => gemGem().map(e => e.toLowerCase()),
		  gemRainbow = _ => [colors[RN(4)],'0'],
		  doubleRainbow = _ => ['0','0'];
	let pair,
		move;
	while (n--){
		pair = RN(6) ? gemGem() : RN(4) ? gemCrash() : RN(2) ? crashCrash() : RN(4) ? gemRainbow() : doubleRainbow();
		if (RN(2)){pair.reverse()}
		move = 'AB'[RN(2)].repeat(RN(4)) + 'RL'[RN(2)].repeat(RN(4));
		r.push([pair.join(''),move])}

	return r;
};
