(defpackage #:challenge/solution
  (:use #:cl)
  (:export #:dosteps))
(in-package #:challenge/solution)

(defmacro dosteps ((var start end step) &body body)
  `(progn
      (let ((start- ,start) (end- ,end) (step- ,step))
      (let  ((,var start-) 
            (direction (if (> step- 0) '< '>)))
            (defun step-test () (funcall direction ,var end-))
            (if (or (not (step-test)) (zerop step-))
              nil
              (loop
                while (step-test)
                do (progn ,@body (incf ,var step-))
              )
            )
      )
      )
    )
)
