def snail(arr)
  res = Array(Int32).new
  until arr.empty?
    arr.shift.each {|w| res << w}
    arr.each {|w| res << w.pop}
    arr.reverse!.map! {|w| w.reverse}
  end
  return res
end

def snail(xs)
  n = xs.size
  nxt, direction = [0], [[-1*n,1], [n,-1]]   #[[up,right],[down,left]]
  return nxt.clear if xs.flatten.size == 0

  (n-1).times{nxt << (nxt[-1]+1)}   # right across top.

  (1...n).each do |a|   #down,left if odd.  up,right if even.
    (n-a).times{nxt << (nxt[-1]+direction[a%2][0])}
    (n-a).times{nxt << (nxt[-1]+direction[a%2][1])}
  end

  nxt.map{|b| xs.flatten[b]}
end

