def factors(m : Int64)
  n_i64 = m.abs
  if (n_i64 < 2)
    return [] of Int64
  end
  f = [] of Int64; i_i64 = 2_i64
  while (i_i64 <= n_i64)
    while (n_i64 % i_i64 == 0)
      f << i_i64
      n_i64 /= i_i64

    end
    i_i64 += 1
  end
  f
end
def list_factors(lst : Array(Int64))
  lst.map { |x_i64| factors(x_i64) }.flatten.uniq
end
def one_sum_of_divided(n : Int64, lst : Array(Int64))
  lst.reduce(0_i64) { |sum_i64, x_i64|
    if (x_i64 % n == 0) 
      sum_i64 += x_i64
    else
      sum_i64
    end
  }
end
def sum_of_divided(lst : Array(Int64))
  list_factors(lst).map { |x_i64| [x_i64, one_sum_of_divided(x_i64, lst)] }.sort
end

def find_primes_up_to(x : Int)
  primes = [2, 3, 5, 7, 11]
  (primes.last + 2).upto(x).step(2).each { |i|
    primes << i if primes.all? { |prime| i % prime != 0 }
  }
  primes
end

def sum_of_divided(lst : Array(Int64))
  return [] of Int64 if lst.empty?
  primes = find_primes_up_to(lst.max_of(&.abs))
  primes.map { |prime|
    found = lst.select{ |num| num % prime == 0 }
    [prime, found.sum] unless found.empty?
  }.compact
end

