def coprime(a, b)
    if (b == 0) 
      return a == 1
    else 
      return coprime(b, a.modulo(b)) 
    end
end

def fromnb2Str(n, sys)
    p = sys.reduce(1){ |m, e| m * e }
    if (p <= n) 
      return "Not applicable" 
    end
    i = 0
    while (i < sys.size)
      j = i + 1
      while (j < sys.size)
        if (!coprime(sys[i], sys[j])) 
          return "Not applicable" 
        end
        j += 1
      end
      i += 1
    end
    res = ""; i = 0
    while (i < sys.size)
      res = res + "-" + n.modulo(sys[i]).to_s + "-"
      i += 1
    end
    res
end

