def fromnb2Str(n, sys)
  return "Not applicable" if sys.reduce(1) { |p, i| p * i } < n || sys.combinations(2).any? { |(a, b)| a.gcd(b) != 1 }
  ret = sys.map { |i| n % i }
  "-#{ret.join("--")}-"
