struct Cipher
  enum Direction
    Left
    Right
  end
  @shift : Int32
  @code : Array(Char) = ('a'..'z').to_a
  def initialize(s : Int32, @dir = Direction::Left)
    @shift = s - 1
  end

  def translate(c : Char)
    @shift += 1
    return c unless c.ascii_letter?
    m = @code.rotate(@dir.left? ? @shift : -@shift)[c.downcase.ord - 97]
    c.lowercase? ? m : m.upcase
  end
end

def moving_shift(s, shift)
  c = Cipher.new(shift)
  coded = s.each_char.join(&->c.translate(Char))
  size = (s.size / 5.0).ceil.to_i
  Array.new(5) {|i| coded[i*size...(i+1)*size] }
end

def demoving_shift(arr, shift)
  c = Cipher.new(shift, Cipher::Direction::Right)
  arr.join.each_char.join(&->c.translate(Char))
end

def shifting(c, n)
  alpha = c >= 'a' ? ('a'..'z') : ('A'..'Z')
  alpha.to_a[(c.ord - alpha.first.ord + n) % alpha.size]
end

def moving_shift(s, shift)
  new_chars = s.chars.map_with_index { |c, i| c.letter? ? shifting(c, shift + i) : c }
  elems = new_chars.each_slice((s.size / 5.0).ceil.to_i).map(&.join).to_a
  Array.new(5) { |i| elems[i]? || "" }
end

def demoving_shift(arr, shift)
  arr.join("").chars.map_with_index { |c, i| c.letter? ? shifting(c, -(shift + i)) : c }.join
end

