  return nil if lst.nil? || lst.empty?
  l = lst.map {|p| p[1]}.reduce(1) {|a, i| a.lcm(i)}
  f = [lst.sum {|p| p[0] * (l/p[1]).to_i}, l]
  return (f[0]/f[1]).to_i if f[0]%f[1] == 0
  d = f[0].gcd(f[1])
  f.map {|i| (i/d).to_i}
end

