def work_on_strings(a, b)
  proc = ->(s : String) { s.downcase.chars.group_by(&.itself).select{|c,a|a.size.odd?}.map{|c,a|c.to_s+c.upcase} }
  au, bu = proc.call(a), proc.call b
  a.tr(bu.join,bu.join.tr(('a'..'z').join+('A'..'Z').join,('A'..'Z').join+('a'..'z').join))+b.tr(au.join,au.join.tr(('a'..'z').join+('A'..'Z').join,('A'..'Z').join+('a'..'z').join))
end

def swapper(a, b)
  cnt = b.downcase.chars.each_with_object(Hash(Char, Int32).new(0)) { |c, h| h[c] += 1 }
  a.chars.join { |c| cnt[c.downcase].even? ? c : c == c.downcase ? c.upcase : c.downcase }
end

def work_on_strings(a, b)
  swapper(a, b) + swapper(b, a)
end

