def john_ann(n)
    john_list = [0]; ann_list = [1]
    res = [ann_list, john_list]
    if (n == 0) 
      return res 
    end
    i = 1
    while (i < n)
        j = john_list[i - 1]
        a1 = ann_list[j]
        john_list << (i - a1)
        a = ann_list[i - 1]
        j1 = john_list[a]
        ann_list<< (i - j1)
        i += 1
    end
    res
end

