def mirror(text)
  words = text.split
  length = words.map{|w| w.size}.max + 2
  words.map!{|w| "*#{" #{w.reverse}".ljust(length, ' ')}*" }
  2.times{|_| words << "".ljust(length + 2, '*')}
  words.rotate!(-1)
  words.join("
")
end

