  words = text.split(" ")
  length = words.map(&.size).max
  frame = "*" * (length + 4)

  words
    .map!(&.reverse)
    .map!{|it| it + " " * (length - it.size)}
    .map!{|it| "* #{it} *
"}

  "#{frame}
#{words.join}#{frame}"
end

