def bits_war(arr)
    odds = 0; evens = 0
    arr.each do |n|
        bitCount = countBits(n.abs)
        if (n % 2 != 0)
            odds += n < 0 ? -bitCount : bitCount;
        else
            evens += n < 0 ? -bitCount : bitCount;
        end
    end
    if (odds > evens)
        return "odds win"
    end
    if (evens > odds)
        return "evens win"
    end
    return "tie"
end

def bits_war(numbers)
  o = numbers.each.select(&.odd?).sum{ |x| x.sign * x.abs.popcount }
  e = numbers.each.select(&.even?).sum{ |x| x.sign * x.abs.popcount }
  case o <=> e
    when .>(0) then "odds win"
    when .<(0) then "evens win"
    else "tie"
  end
end

def bits_war(numbers)
  evens = numbers.select{|a| a%2==0}.sum {|n| n.to_s(2).count('1') * (n<0 ? -1 : 1) }
	odds  = numbers.select{|a| a%2!=0}.sum {|n| n.to_s(2).count('1') * (n<0 ? -1 : 1) }
	return (evens == odds ? "tie" : evens > odds ? "evens win" : "odds win")
end

