58e24788e24ddee28e000053.body:
class Program
  def initialize(@program : Array(String))
    @ptr = 0
    @registers = Hash(String, Int32).new
  end

  def run
    puts "Running program"
    loop do
      break if @ptr >= @program.size
      instruction = @program[@ptr].split
      case instruction[0]
      when "mov"
        mov instruction[1], instruction[2].to_i? || instruction[2]
      when "inc"
        inc instruction[1]
      when "dec"
        dec instruction[1]
      when "jnz"
        jnz instruction[1].to_i? || instruction[1], instruction[2].to_i
        next
      end
      @ptr += 1
    end
    @registers
  end

  def mov(register, value : String)
    @registers[register] = @registers[value]
  end

  def mov(register, value : Int32)
    @registers[register] = value
  end

  def inc(register)
    @registers[register] += 1
  end

  def dec(register)
    @registers[register] -= 1
  end

  def jnz(value : Int32, by)
    @ptr += (value != 0 ? by : 1)
  end

  def jnz(register : String, by)
    jnz(@registers[register], by)
  end
end

def simple_assembler(program)
  Program.new(program).run
end

