  return "Not valid" if !num.is_a?(Int32) || num < 0
  return "No palindromes found" if num < 10
  n = num.to_s
  c = {} of Int32 => Int32
  n.size.times { |i| i.times { |j| 
    s=n[j..i]; c[s.to_i] = 1 if s[0]!='0' && s[-1]!='0' && s == s.reverse
  }}
