    scoring = {
      "Wild":   10,
      "Star":   9,
      "Bell":   8,
      "Shell":  7,
      "Seven":  6,
      "Cherry": 5,
      "Bar":    4,
      "King":   3,
      "Queen":  2,
      "Jack":   1,
    }

    values = reels.map_with_index { |reel, i| reel[spins[i]] }
    counts = Hash(String, Int32).new { |h, k| h[k] = 0 }
    values.each { |v| counts[v] += 1 }

    relevant_values = counts.to_a.select { |v| v[1] > 1 }
    return 0 if relevant_values.size == 0

    image, count = relevant_values.first
    multiplier = count == 3 ? 10 : counts["Wild"] == 1 ? 2 : 1
    scoring[image] * multiplier
end

def fruit(reels, spins)
  a = {"Ja", "Qu", "Ki", "Ba", "Ch", "Se", "Sh", "Be", "St", "Wi"}
