  h = result.index_by{ |x| result.count(x) }
  if value = a.index (h[2]? || h[3]? || "")[0..1]
    value += 1
    case h.key("Wild") { 0 }
    when 0, 2, 3
      h[3]? ? value * 10 : value
    else # 1
      value * 2
    end
  else
    0
  end
end

