  key_s = key.chars.sort
  mapping0 = key_s.map_with_index { |ch,i| {ch, i+1} }.to_h
  mapping1 = key.chars.map { |ch| mapping0[ch] }

  ret = [] of String
  msg.chars.each_slice( key.size ) { |s| 
    p = ( mapping1
