class Projectile
  def initialize(h : Float64, v0 : Float64, a : Float64)
    @start_height = h
    @start_velocity = v0
    @angle = a
    @horiz_vel = Float64.new(@start_velocity * Math.cos((a * Math::PI) / 180.0))
    @vertical_vel = Float64.new(@start_velocity * Math.sin((a * Math::PI) / 180.0))
  end
  def height_eq
    "h(t) = -16.0t^2 + #{@vertical_vel.round(3)}t" + (@start_height == 0 ? "" : " + #{@start_height.to_f}")
  end
  def horiz_eq
    "x(t) = #{@horiz_vel.round(3)}t"
  end
  def height(time)
    (-16 * (time ** 2) + (@vertical_vel * time) + @start_height).round 3
  end
  def horiz(time)
    (@horiz_vel * time).round 3
  end
  def landing
    t = ((0-@vertical_vel - Math.sqrt((@vertical_vel**2)-(4*(-16)*@start_height)))/-32.0)
    x = horiz(t)
    return [x.round(3), 0, t.round(3)]
  end
end

