def get_order(input : String)
  items = ["burger", "fries", "chicken", "pizza", "sandwich", "onionrings", "milkshake", "coke"]
  items.flat_map do |item|
    [item.capitalize] * input.downcase.scan(item).size
  end.join(" ")
end

def get_order(input : String)
  items = "Burger Fries Chicken Pizza Sandwich Onionrings Milkshake Coke".split.map { |i| i.downcase }
  result = [] of String
  items.each { |i|
    input.scan(/#{i}/).size.times do
      result << i.capitalize
    end
  }
  result.join(" ")
end

def get_order(input : String)
  menu = ["Burger", "Fries", "Chicken", "Pizza", "Sandwich", "Onionrings", "Milkshake", "Coke"]
  input.scan(/milkshake|pizza|chicken|fries|coke|burger|sandwich|onionrings/i).map(&.[0].capitalize).sort_by do |x|
    menu.index(x) || -1
  end.join(" ")
end

