def odd_ones_out(a)
  count=Hash(Int32, Int32).new(0)
  a.each{|x| count[x]+=1}
  a.select{|x| count[x].even?}
end

def odd_ones_out(numbers)
  fs = numbers.tally
  numbers.select{ |n| fs[n].even? }
end

def odd_ones_out(numbers)
  totals = Hash(Int32,Int32).new(0)
  numbers.each{|x| totals[x] += 1}
  numbers.select{|x| totals[x].even? }
end

def odd_ones_out(numbers)
  new_array = Array(Int32).new
  numbers.each { |n|  new_array << n if numbers.count(n).odd? }
  numbers - new_array
end

