def consecutive_ducks(n : Int64)
  n % 2 != 0 ? n != 1 : consecutive_ducks(n >> 1)
end

def consecutive_ducks(n : Int64)
  Math.log2(n) != Math.log2(n).floor
end

def check_sum(n, sz)
  return false if sz > n
  z = ((0 + sz - 1) * sz).tdiv(2)
  n % sz == z % sz
end

def consecutive_ducks(n : Int64)
  max = (n ** 0.5).ceil
  return (2..max).any?{|sz| check_sum(n, sz) }
end

