def flip(dir, box)
  case dir
  when 'L' then box.map { |row| row.sort.reverse }
  when 'R' then box.map { |row| row.sort }
  when 'U' then box.transpose.map { |row| row.sort.reverse }.transpose
  when 'D' then box.transpose.map { |row| row.sort }.transpose
  end
end

