def burner(c : Int32, h : Int32, o : Int32) : Tuple(Int32, Int32, Int32)
  h2o = [o, h // 2].min
  co2 = [c, (o - h2o) // 2].min
  ch4 = [c - co2, (h - h2o * 2) // 4].min
  {h2o, co2, ch4}
end

def burner(c : Int32, h : Int32, o : Int32) : Tuple(Int32, Int32, Int32)
    water = Math.min(h // 2,  o)
    co2 = Math.min((o - water) // 2, c)
    methane = Math.min((h - water * 2) // 4, c - co2)
    {water, co2, methane}
end

def burner(c : Int32, h : Int32, o : Int32) : Tuple(Int32, Int32, Int32)
  water = [h // 2, o].min
  carbon = [(o - water) // 2, c].min
  methane = [(h - water * 2) // 4, c - carbon].min
  {water, carbon, methane}
end

