def count_salutes(hallway)
  hallway.each_char.with_index.reduce(0){|s, (c, i)| s + (c == '>' ? 2 * hallway[i..-1].count('<') : 0) }
end

def count_salutes(hallway)
  hallway.each_char.reduce({0, 0}){ |(t, r), c|
    case c
      when '>' then {t, r + 1}
      when '<' then {t + r, r}
      when '-' then {t, r}
      else raise ArgumentError.new("Unknown character")
    end
  }[0] * 2
end

