module RomanNumerals
const numerals = [
  ("M", 1000);
  ("CM", 900);
  ("D", 500);
  ("CD", 400);
  ("C", 100);
  ("XC", 90);
  ("L", 50);
  ("XL", 40);
  ("X", 10);
  ("IX", 9);
  ("V", 5);
  ("IV", 4);
  ("I", 1)
]

function to_roman(number::Int64)::String
  str = ""
  num = number
  for pair in numerals
    while num >= pair[2]
      num -= pair[2]
      str *= pair[1]
    end
  end
  return str
end

function from_roman(string::String)::Int64
  num = 0
  str = string
  for pair in numerals
    while startswith(str, pair[1])
      str = str[length(pair[1])+1:end]
      num += pair[2]
    end
  end
  return num
end

end

