function sum_of_intervals(intervals::Vector{Tuple{Int, Int}})
    numbers = []
    for (s, e) in intervals
        # push!(numbers, collect(s:e-1))
        for num in collect(s:e-1)
            push!(numbers, num)
        end
    end

    return length(Set(numbers))
end

function sum_of_intervals(a)
  sort!(a,by=x->x[1])
  i=1
  while i<length(a)
      if(a[i][2]>=a[i+1][1])
          a[i] = (a[i][1],max(a[i][2],a[i+1][2]))
          popat!(a,i+1)
      else
          i += 1
      end
  end
  return sum(x->x[2]-x[1],a)
end

