function spiralize(s::Int)::Matrix{Int}
  if s == 2
    return [1 1; 0 1]
  elseif s == 3
    return [1 1 1;0 0 1; 1 1 1]
  else
    return [
      fill(1,s)';
      delta(s)';
      [rot180(spiralize(s-2)) delta(s-2) fill(1,s-2)]
    ]
  end
end

function delta(s::Int)
  return [fill(0, s-1);1]
end

