function longest_slide_down(pyramid::Vector{Vector{Int}})
  l = length(pyramid)
  for i in 1:(l-1)
    for j in 1:length(pyramid[l-i])
      pyramid[l-i][j] += max(pyramid[l+1-i][j],pyramid[l+1-i][j+1])
    end
  end
  return pyramid[1][1]
end

