"Iterate over the neighbouring cells in N dimensions."
function eachneighbour(cells, coord::CartesianIndex{N}) where {N}
    (
        cells[neighbourcoord]
        for neighbourcoord in (
            coord + c
            for c in CartesianIndices(Tuple(repeat([-1:1], N)))
            if !iszero(c)
        )
        if neighbourcoord in keys(cells)
    )
end

function gameoflife(cell, neighbours)
    score = sum(neighbours)
    score == 3 || cell == 1 && score == 2
end

