module JohnAnn
    export john_ann, john, ann, sum_john, sum_ann

    function john_ann(n)
#       kata = Dict("john" => [0], "ann" => [1])
      j = fill(0,n)
      a = fill(1,n)

      for day in 1:n-1
        j[day+1] = day - a[j[day]+1]
        a[day+1] = day - j[a[day]+1]
      end

      return (j, a)
    end

    function john(n)
      return john_ann(n)[1]
    end

    function ann(n)
      return john_ann(n)[2]
    end

    function sum_john(n)
      return sum(john(n))
    end

    function sum_ann(n)
      return sum(ann(n))
    end
end

module JohnAnn
    export john_ann, john, ann, sum_john, sum_ann

    A = Dict{Int, Int}(0 => 1)
    J = Dict{Int, Int}(0 => 0)

    function a(n)
        n >= 0 || return 0
        haskey(A, n) && return A[n]
        k = n - j(a(n - 1))
        A[n] = k
        return k
    end

    function j(n)
        n >= 0 || return 0
        haskey(J, n) && return J[n]
        k = n - a(j(n - 1))
        J[n] = k
        return k
    end

    function ann(n)
        return a.(0:(n-1)) 
    end

    function john(n)
        return j.(0:(n-1))
    end

    function sum_ann(n)
        return sum(ann(n))
    end

    function sum_john(n)
        return sum(john(n))
    end

end

