module Fibo
    export length_sup_u_k, comp
    arr = [1,1,2,3,3,4,5,5,6,6,6,8,8,8,10,9,10,11,11,12,12,12,12]
    function get_n(n)
        while (n>(i=length(arr)))
            @inbounds push!(arr,arr[i+1-arr[i]]+arr[i+1-arr[i-1]])
        end
    end
    function length_sup_u_k(n, k)
        if length(arr)<n
            get_n(n);
        end
        sum(arr[1:n] .>= k)
    end
    function comp(n)
        if length(arr)<n
            get_n(n);
        end
        sum(arr[1:n-1] .> arr[2:n])
    end

end

