58e24788e24ddee28e000053.body:
# Is this overkill? Probably. Did it help me learn multiple dispatch? Definitely.

abstract type AbstractInstruction end

struct MoveInstruction{T} <: AbstractInstruction
    target::AbstractString
    value::T
end

struct DecInstruction <: AbstractInstruction
    target::AbstractString
end

struct IncInstruction <: AbstractInstruction
    target::AbstractString
end

struct JumpInstruction{T, V} <: AbstractInstruction
    target::T
    value::V
end

mutable struct ProgramState
    registry::Dict{String, Number}
    cursor::Int
end

function parseifable(value::Any)::Union{Int, Any}
    return isnothing(tryparse(Int, value)) ? value : parse(Int, value);
end

function parseinstruction(instruction::String)::AbstractInstruction
    parts = split(instruction, " ");
    if parts[1] == "mov"
        return MoveInstruction(parts[2], parseifable(parts[3]));
    end
    if parts[1] == "dec"
        return DecInstruction(parts[2]);
    end
    if parts[1] == "inc"
        return IncInstruction(parts[2]);
    end
    if parts[1] == "jnz"
        return JumpInstruction(parseifable(parts[2]), parseifable(parts[3]));
    end
end

function runinstruction!(instr::MoveInstruction{SubString{String}}, state::ProgramState)
    state.registry[instr.target] = state.registry[instr.value];
    state.cursor += 1;
end

function runinstruction!(instr::MoveInstruction{Int}, state::ProgramState)
    state.registry[instr.target] = instr.value;
    state.cursor += 1;
end

function runinstruction!(instr::DecInstruction, state::ProgramState)
    state.registry[instr.target] -= 1;
    state.cursor += 1;
end

function runinstruction!(instr::IncInstruction, state::ProgramState)
    state.registry[instr.target] += 1;
    state.cursor += 1;
end

function runinstruction!(instr::JumpInstruction{SubString{String}, SubString{String}},
                         state::ProgramState)
    if state.registry[instr.target] != 0
        state.cursor += state.registry[instr.value];
    else
        state.cursor += 1;
    end
end

function runinstruction!(instr::JumpInstruction{SubString{String}, Int}, state::ProgramState)
    if state.registry[instr.target] != 0
        state.cursor += instr.value;
    else
        state.cursor += 1;
    end
end

function runinstruction!(instr::JumpInstruction{Int, SubString{String}}, state::ProgramState)
    if instr.target != 0
        state.cursor += state.registry[instr.value];
    else
        state.cursor += 1;
    end
end

function runinstruction!(instr::JumpInstruction{Int, Int}, state::ProgramState)
    if instr.target != 0
        state.cursor += instr.value;
    else
        state.cursor += 1;
    end
end

function simple_assembler(program::Array{String, 1})::Dict{String, Number}
    state = ProgramState(Dict{String, Number}(), 1);
    instructions = parseinstruction.(program);
    while state.cursor <= length(instructions)  
        # println(state);
        runinstruction!(instructions[state.cursor], state);
    end
    return state.registry;
end

