module Kata
export showdigits

function showdigits(digits::AbstractString, size::Integer)::String

  n = size

  line1 = (i,j) -> (i==1 && 2<=j<=(n+1), "-")
  line2 = (i,j) -> (i==(n+2) && 2<=j<=(n+1), "-")
  line3 = (i,j) -> (i==2n+3 && 2<=j<=(n+1), "-")
  line4 = (i,j) -> (2<=i<=(n+1) && j==1, "|")
  line5 = (i,j) -> ((n+3)<=i<=(2n+2) && j==1, "|")
  line6 = (i,j) -> (2<=i<=(n+1) && j==(n+2), "|")
  line7 = (i,j) -> ((n+3)<=i<=(2n+2) && j==(n+2), "|")

  make_lines_f = (x) -> (
    x=="1" && return (line6,line7);
    x=="2" && return (line1,line2,line3,line5,line6);
    x=="3" && return (line1,line2,line3,line6,line7);
    x=="4" && return (line2,line4,line6,line7);
    x=="5" && return (line1,line2,line3,line4,line7);
    x=="6" && return (line1,line2,line3,line4,line5,line7);
    x=="7" && return (line1,line6,line7);
    x=="8" && return (line1,line2,line3,line4,line5,line6,line7);
    x=="9" && return (line1,line2,line3,line4,line6,line7);
    x=="0" && return (line1,line3,line4,line5,line6,line7);
  )

  function pos_val(i,j, fs)
    for f in fs
      f(i,j)[1]==true && return f(i,j)[2]
    end
    return " "
  end

  function make_lines(s)
    return [join((pos_val(i,j, make_lines_f(s)) for j=1:(n+2)),"") for i=1:(2n+3)]
  end

  combined = [make_lines(string(c)) for c in digits]
  return join(
        (rstrip(join((combined[k][i] for k=1:length(digits)),"")) for i=1:(2n+3)),
        "
")

end

end # module

