function nines(n :: BigInt) :: BigInt
  if n == big(9)
    return big(1)
  elseif n < big(10)
    return big(0)
  else
    d = length(string(n))
    b = big(10) ^ (d - 1)
    a = n ÷ b
    if a == 9
      return a * k(d - 1) + 1 + n  - a * b
    else
      return a * k(d - 1) + nines(n % b)
    end
  end
end

function k(d)
  if d <= 1
    return big(d)
  else
    return big(9) * k(d - 1) + big(10) ^ (d - 1)
  end
end

