function largest_power(n::Int)

    if n == 1
        return (0, -1)
    elseif n ≤ 4
        return (1, -1)
    end

    pwrs = [ base^floor(Int, log(base, n-1)) for base in 2:floor(Int, √(n-1))]
    m = maximum(pwrs)

    (m, count(x->x==m, pwrs))
end

